import React, { useState, useEffect } from 'react';

const GuestRestaurantOrders = () => {
    const [menu, setMenu] = useState([]); // Initialize as an empty array
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchMenu = async () => {
            const branchId = localStorage.getItem('selectedBranchId'); // Retrieve branchId from localStorage
            console.log('Retrieved branchId:', branchId); // Debugging log

            if (!branchId) {
                setError('Branch ID is missing. Please select a branch.');
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/api/menu?branchId=${branchId}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                });
                const data = await response.json();

                if (response.ok && Array.isArray(data)) {
                    setMenu(data); // Set menu only if data is an array
                } else {
                    setError('Failed to fetch menu.');
                }
            } catch (err) {
                console.error('Error fetching menu:', err);
                setError('Something went wrong. Please try again later.');
            }
        };

        fetchMenu();
    }, []);

    const handleOrder = async (menuItemId) => {
        try {
            const response = await fetch('http://localhost:5000/api/order', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
                body: JSON.stringify({ menuItemId }),
            });

            if (response.ok) {
                alert('Order placed successfully!');
            } else {
                alert('Failed to place order.');
            }
        } catch (err) {
            console.error('Error placing order:', err);
            alert('Something went wrong. Please try again later.');
        }
    };

    return (
        <div>
            <h1>Restaurant Menu</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {menu.length > 0 ? (
                    menu.map((item) => (
                        <li key={item.menu_item_id}>
                            {item.item_name} - ${item.item_price}
                            <button onClick={() => handleOrder(item.menu_item_id)}>Order</button>
                        </li>
                    ))
                ) : (
                    <p>No menu items available.</p>
                )}
            </ul>
        </div>
    );
};

export default GuestRestaurantOrders;
