import React, { useState, useEffect } from 'react';

const GuestHotelFacilities = () => {
    const [facilities, setFacilities] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchFacilities = async () => {
            const branchId = parseInt(localStorage.getItem('selectedCity'), 10); // Ensure branchId is an integer
            if (!branchId) {
                setError('Invalid branch selected.');
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/api/facilities?branchId=${branchId}`, {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                });
                const data = await response.json();

                if (response.ok) {
                    setFacilities(Array.isArray(data) ? data : []);
                } else {
                    setError('Failed to fetch facilities.');
                }
            } catch (err) {
                console.error('Error fetching facilities:', err);
                setError('Something went wrong. Please try again later.');
            }
        };

        fetchFacilities();
    }, []);

    const handleUseFacility = async (facilityId) => {
        try {
            const response = await fetch('http://localhost:5000/api/use-facility', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
                body: JSON.stringify({ facilityId }),
            });

            if (response.ok) {
                alert('Facility used successfully!');
            } else {
                alert('Failed to use facility.');
            }
        } catch (err) {
            console.error('Error using facility:', err);
        }
    };

    return (
        <div>
            <h1>Hotel Facilities</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {facilities.length > 0 ? (
                    facilities.map((facility) => (
                        <li key={facility.facility_id}>
                            {facility.facility_name} - â‚¹{facility.usage_fee}
                            <button onClick={() => handleUseFacility(facility.facility_id)}>Use</button>
                        </li>
                    ))
                ) : (
                    <p>No facilities available.</p>
                )}
            </ul>
        </div>
    );
};

export default GuestHotelFacilities;
