import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const GuestCitySelection = () => {
    const [branches, setBranches] = useState([]);
    const [selectedBranch, setSelectedBranch] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchBranches = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/branches');
                const data = await response.json();
                setBranches(data);
            } catch (err) {
                console.error('Error fetching branches:', err);
            }
        };

        fetchBranches();
    }, []);

    const handleBranchSelection = () => {
        if (selectedBranch) {
            localStorage.setItem('selectedCity', selectedBranch); // Store branch_id instead of branch_name
            navigate('/guest/room-booking');
        } else {
            alert('Please select a branch.');
        }
    };

    return (
        <div>
            <h1>Select Branch</h1>
            <select value={selectedBranch} onChange={(e) => setSelectedBranch(e.target.value)}>
                <option value="">Select a branch</option>
                {branches.map((branch) => (
                    <option key={branch.branch_id} value={branch.branch_id}>
                        {branch.branch_name}
                    </option>
                ))}
            </select>
            <button onClick={handleBranchSelection}>Next</button>
        </div>
    );
};

export default GuestCitySelection;
